import React from 'react';
import sendLogo from './send.png';
import './index.css';
import Users from './Users';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

export default function Home() {
    // listening on type
    const [inputValue, setInputValue] = React.useState('');
    const [user, setUser] = React.useState(false);
    const handleChange = (e) => {
        setInputValue(e.target.value);
    }
    // sending message to backend
    const sendMessage = () => {
        fetch('/sendMessage', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ message: inputValue })
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
            })
            .catch(error => console.error('Error sending message:', error));
    }
    // get all users
    let usersData;

    const getUsers = () => {
        fetch('/getUsers')
            .then(response => response.json())
            .then(data => {
                usersData = data.map((user) => {
                    return <Users data={user} />

                });

            })
            .catch(error => console.error('Error fetching users:', error));
    }
    getUsers()
    return (
        <div className="container">
            <Users data={usersData} />
            <div className='header'>
            </div>

            <div className='typingBar'>
                <input type='text' placeholder='Type something...' className='input' onChange={(e) => handleChange(e)} />
                <div className='sendButton' onClick={sendMessage}>
                    <img src={sendLogo} alt='send button' className='sendBtn' />
                </div>
            </div>
        </div>
    )
}
