import React from 'react';
import sendLogo from './send.png';
import './index.css';
import Users from './Users';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

export default function Home(props) {
    // listening on type
    const [inputValue, setInputValue] = React.useState('');
    const handleChange = (e) => {
        setInputValue(e.target.value);
    }
    // sending message to backend
    const sendMessage = () => {
        fetch('/sendMessage', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ message: inputValue, receiver: localStorage.getItem('receiver'), sender: localStorage.getItem('currUser') })
        })
            .then(response => response.json())
            .then(data => {
                if (data.success === true) {
                    console.log('Message sent');

                } else {
                    console.log('Message not sent');
                }
            })
            .catch(error => console.error('Error sending message:', error));
    }


    // get all users
    const [usersData, setUsersData] = React.useState([]);

    React.useEffect(() => {
        const getUsers = () => {
            fetch('/getUsers')
                .then(response => response.json())
                .then(data => {
                    setUsersData(data);
                })
                .catch(error => console.error('Error fetching users:', error));
        };
        getUsers();
    }, []);

    // get all messages between two people
    const [messages, setMessages] = React.useState([]);
    function getMessages() {
        fetch('/getMessages', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ receiver: localStorage.getItem('receiver'), sender: localStorage.getItem('currUser') })
        })
            .then(response => response.json())
            .then(data => {
                // console.log(data);
                setMessages(data);
                createMessages(messages);
            })
            .catch(error => console.error('Error fetching messages:', error));

    }
    const [messagesHTML, setMessagesHTML] = React.useState([]);
    function createMessages(messages) {
        // console.log(messages);
        const messagesHTML = (
            <>
                {/* <div className='messagesArea'> */}
                {messages.map(message => (
                    <div key={message.id} className='message' data-sender={message.author} data-receiver={message.receiver}>
                        <p>{message.content}</p>
                    </div>
                ))}
                {/* </div> */}
            </>
        );
        setMessagesHTML(messagesHTML);
        const messages_to_filter = document.querySelectorAll('.message');
        messages_to_filter.forEach(message => {
            if (message.getAttribute('data-sender') === localStorage.getItem('currUser')) {
                message.classList.add('currentUserMessage');
            }
        });
        console.log(messagesHTML);
        // console.log(messagesHTML);
        // document.querySelector('.messagesArea').innerHTML = messagesHTML;
    }
    return (
        <div className="container">
            <div className='usersSideBar'>
                <div className='header-name'>
                    <h2>Contacts</h2>
                </div>
                {usersData.map(user => <Users key={user.id} data={user} getMessagesFunc={getMessages} />)}
            </div>
            <div className='header'>
            </div>
            <div className='messagesArea'>
                {messagesHTML}
            </div>
            <div className='typingBar'>
                <input type='text' placeholder='Type something...' className='input' onChange={(e) => handleChange(e)} />
                <div className='sendButton' onClick={sendMessage}>
                    <img src={sendLogo} alt='send button' className='sendBtn' />
                </div>
            </div>
        </div>
    )
}
