import React from 'react';
import './Users.css'
import Home from './Home';


export default function Users({ data }) {
    const receiverSave = (e) => {
        localStorage.setItem('receiver', e.target.id);
    }
    // fetch messages when clicked on a user
    const [messages, setMessages] = React.useState([]);
    function getMessages() {
        fetch('/getMessages', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ receiver: data.username, sender: localStorage.getItem('currUser') })
        })
            .then(response => response.json())
            .then(data => {
                // console.log(data);
                setMessages(data);
                createMessages(messages);
            })
            .catch(error => console.error('Error fetching messages:', error));

    }

    function handleClick(e) {
        receiverSave(e);
        getMessages();
        // <Home messages={messages} />
    }
    function createMessages(messages) {
        console.log(messages);
        const messagesHTML = messages.map(message => {
            return (
                <div key={message.id} className='message' id={message.sender}>
                    <p>{message.message}</p>
                </div>
            )
        })
        console.log(messagesHTML);
    }
    return (
        <>
            {/* <Home messages={messages} /> */}
            {

                <div key={data.key} className='user' id={data.username} onClick={(e) => handleClick(e)} >
                    <p>{data.username}</p>
                </div>
            }
        </>
    )
}
