import React from 'react';
import sendLogo from './send.png';
import './index.css';
import Users from './Users';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

export default function Home() {
    // listening on type
    const [inputValue, setInputValue] = React.useState('');
    const [user, setUser] = React.useState(false);
    const handleChange = (e) => {
        setInputValue(e.target.value);
    }
    // sending message to backend
    const sendMessage = () => {
        fetch('/sendMessage', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ message: inputValue })
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
            })
            .catch(error => console.error('Error sending message:', error));
    }
    // get all users
    const [usersData, setUsersData] = React.useState([]);

    React.useEffect(() => {
        const getUsers = () => {
            fetch('/getUsers')
                .then(response => response.json())
                .then(data => {
                    setUsersData(data);
                })
                .catch(error => console.error('Error fetching users:', error));
        };
        getUsers();
    }, []);
    // save the receiver's username/id in local storage
    const receiverSave = (e) => {
        localStorage.setItem('receiver', e.target.id);
    }
    return (
        <div className="container">
            <div className='usersSideBar'>
                {usersData.map(user => <Users key={user.id} data={user} onClick={(e) => receiverSave(e)} />)}
            </div>
            <div className='header'>
            </div>

            <div className='typingBar'>
                <input type='text' placeholder='Type something...' className='input' onChange={(e) => handleChange(e)} />
                <div className='sendButton' onClick={sendMessage}>
                    <img src={sendLogo} alt='send button' className='sendBtn' />
                </div>
            </div>
        </div>
    )
}
